mapper.select:
select 

,

from LAB_EQUIPMENT
where LABSEQNO = #{labseqno,jdbcType=DECIMAL}
  and EQUIPSEQNO = #{equipseqno,jdbcType=DECIMAL}

select

,

from LAB_EQUIPMENT
where CHINESENAME= #{chinesename,jdbcType=VARCHAR}
AND WHICHLAB=#{whichlab,jdbcType=VARCHAR}
AND RAWVALUE=#{rawvalue,jdbcType=DECIMAL}

mapper.delete:
delete from LAB_EQUIPMENT
where LABSEQNO = #{labseqno,jdbcType=DECIMAL}
  and EQUIPSEQNO = #{equipseqno,jdbcType=DECIMAL}
